import { onMounted, ref } from 'vue';

type Theme = 'light' | 'dark';

const storageKey = 'theme-preference';

const currentTheme = ref<Theme>('light');

function getSystemPreference(): Theme {
  if (typeof window !== 'undefined' && window.matchMedia) {
    return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
  }
  return 'light';
}

function applyTheme(theme: Theme) {
  currentTheme.value = theme;
  if (typeof document !== 'undefined') {
    const root = document.documentElement;
    root.classList.toggle('dark', theme === 'dark');
    root.setAttribute('data-theme', theme);
  }
  if (typeof localStorage !== 'undefined') {
    localStorage.setItem(storageKey, theme);
  }
}

export function useTheme() {
  const setTheme = (theme: Theme) => applyTheme(theme);
  const toggleTheme = () => applyTheme(currentTheme.value === 'dark' ? 'light' : 'dark');

  onMounted(() => {
    const saved = (typeof localStorage !== 'undefined' ? localStorage.getItem(storageKey) : null) as Theme | null;
    const initial = saved || getSystemPreference();
    applyTheme(initial);
  });

  return {
    currentTheme,
    setTheme,
    toggleTheme,
  };
}

export function cn(
  ...classes: Array<string | null | undefined | false>
): string {
  return classes.filter(Boolean).join(' ');
}

import { ref, computed } from 'vue';

export interface CartItem {
  id: string | number;
  title?: string;
  brand?: string;
  quantity: number;
  // Campos adicionais para exibir no modal do carrinho
  categoria?: string;
  diametro_montagem?: string;
  conexao_instrumento?: string;
  faixa_trabalho?: string;
  unidade_leitura?: string;
  notes?: string;
}

const items = ref<CartItem[]>([]);

// Load from localStorage (client-only)
if (typeof window !== 'undefined') {
  try {
    const saved = window.localStorage.getItem('cart-items');
    if (saved) {
      const parsed: CartItem[] = JSON.parse(saved);
      if (Array.isArray(parsed)) items.value = parsed.filter(Boolean);
    }
  } catch (_) {
    // ignore
  }
}

function persist() {
  if (typeof window !== 'undefined') {
    try {
      window.localStorage.setItem('cart-items', JSON.stringify(items.value));
    } catch (_) {
      // ignore
    }
  }
}

function add(product: { id: CartItem['id']; title?: string; brand?: string; categoria?: string; diametro_montagem?: string; conexao_instrumento?: string; faixa_trabalho?: string; unidade_leitura?: string }, qty = 1) {
  if (!product || product.id == null) return { ok: false as const, reason: 'invalid' as const };
  const found = items.value.find((p) => p.id === product.id);
  if (found) {
    found.quantity += Math.max(1, qty);
  } else {
    items.value.push({
      id: product.id,
      title: product.title,
      brand: product.brand,
      categoria: product.categoria,
      diametro_montagem: product.diametro_montagem,
      conexao_instrumento: product.conexao_instrumento,
      faixa_trabalho: product.faixa_trabalho,
      unidade_leitura: product.unidade_leitura,
      quantity: Math.max(1, qty),
    });
  }
  persist();
  return { ok: true as const };
}

function decrement(id: CartItem['id'], qty = 1) {
  const found = items.value.find((p) => p.id === id);
  if (!found) return;
  found.quantity = Math.max(0, found.quantity - Math.max(1, qty));
  if (found.quantity === 0) items.value = items.value.filter((p) => p.id !== id);
  persist();
}

function remove(id: CartItem['id']) {
  items.value = items.value.filter((p) => p.id !== id);
  persist();
}

function clear() {
  items.value = [];
  persist();
}

const count = computed(() => items.value.reduce((sum, p) => sum + (p.quantity || 0), 0));
const has = (id: CartItem['id']) => items.value.some((p) => p.id === id);
function setQuantity(id: CartItem['id'], qty: number) {
  const found = items.value.find((p) => p.id === id);
  if (!found) return;
  found.quantity = Math.max(1, Number.isFinite(qty) ? qty : 1);
  persist();
}
function setNotes(id: CartItem['id'], notes: string) {
  const found = items.value.find((p) => p.id === id);
  if (!found) return;
  found.notes = String(notes ?? '');
  persist();
}

export function useCart() {
  return { items, add, remove, decrement, clear, count, has, setQuantity, setNotes };
}

import { ref, computed, watch } from 'vue';

// Controla visibilidade global de overlays/modais para ocultar o cabealho
const counter = ref(0);

const isOpen = computed(() => counter.value > 0);

function open() {
  counter.value += 1;
}

function close() {
  counter.value = Math.max(0, counter.value - 1);
}

function set(openState: boolean) {
  counter.value = openState ? Math.max(1, counter.value) : 0;
}

// Classe no body para possveis estilos globais quando overlay estiver aberto
watch(isOpen, (v) => {
  try {
    const cls = 'overlay-open';
    if (v) document.body.classList.add(cls);
    else document.body.classList.remove(cls);
  } catch (_) {
    // ambiente sem DOM (SSR)
  }
});

export function useOverlay() {
  return { overlayOpen: isOpen, open, close, set };
}

import { ref, computed } from 'vue';

export interface UserProfilePayload {
  fullName: string;
  sector: string;
  whatsapp: string;
}

// Estados compartilhados ao nvel de mdulo (uma instncia para todo o app)
const email = ref('');
const empresaId = ref<number | null>(null);
const fullName = ref('');
const sector = ref('');
const whatsapp = ref('');
let initialized = false;

function getStorageKey(e?: string, emp?: number | null) {
  const emailKey = ((e ?? email.value) ?? '').trim()
  const empresaKey = emp ?? empresaId.value
  if (emailKey && empresaKey != null && Number.isFinite(empresaKey)) {
    return `userProfile:${emailKey}:${empresaKey}`
  }
  return 'userProfile'
}

function initLoad() {
  if (initialized || typeof window === 'undefined') return;
  initialized = true;
  try {
    const e = localStorage.getItem('userEmail');
    if (e) email.value = e;
    const emp = localStorage.getItem('empresaId');
    if (emp) {
      const parsed = Number(emp);
      empresaId.value = Number.isFinite(parsed) ? parsed : null;
    }
    // Carrega do storage chaveado por usurio (email+empresa). Evita vazamento entre usurios.
    const key = getStorageKey();
    const stored = localStorage.getItem(key);
    if (stored) {
      const obj = JSON.parse(stored || '{}');
      fullName.value = obj.fullName || '';
      sector.value = obj.sector || '';
      whatsapp.value = obj.whatsapp || '';
    }
  } catch (_) {
    // silent
  }
}

function save() {
  if (typeof window === 'undefined') return;
  try {
    const key = getStorageKey();
    localStorage.setItem(key, JSON.stringify({
      fullName: fullName.value,
      sector: sector.value,
      whatsapp: whatsapp.value,
    }));
  } catch (_) {
    // silent
  }
}

function setEmail(e: string) {
  email.value = e || '';
  if (typeof window !== 'undefined') {
    try {
      localStorage.setItem('userEmail', email.value);
      // limpar storage legado no chaveado
      try { localStorage.removeItem('userProfile'); } catch (_) {}
    } catch (_) {}
  }
}

function setEmpresaId(id: number | null | undefined) {
  const val = typeof id === 'number' && Number.isFinite(id) ? id : null;
  empresaId.value = val;
  if (typeof window !== 'undefined') {
    try {
      localStorage.setItem('empresaId', val != null ? String(val) : '');
      // limpar storage legado no chaveado
      try { localStorage.removeItem('userProfile'); } catch (_) {}
    } catch (_) {}
  }
}

const whatsappDigits = computed(() => (whatsapp.value || '').replace(/\D/g, ''));
// Critrio de completude: apenas os campos do modal (nome, setor, whatsapp)
const isComplete = computed(() => {
  return fullName.value.trim().length >= 3 && sector.value.trim().length >= 2 && whatsappDigits.value.length === 13;
});
const isIncomplete = computed(() => !isComplete.value);

function saveProfile(payload: UserProfilePayload) {
  fullName.value = (payload.fullName || '').trim();
  sector.value = (payload.sector || '').trim();
  whatsapp.value = (payload.whatsapp || '').trim();
  save();
}

async function refreshFromServer() {
  // Busca no backend o perfil do usurio atual (email+empresa)
  const e = (email.value || '').trim();
  const emp = empresaId.value;
  if (!e || emp == null || !Number.isFinite(emp)) return;
  try {
    const params = new URLSearchParams({ email: e, empresaId: String(emp) });
    const resp = await fetch(`/api/user/profile?${params.toString()}`);
    const data = await resp.json();
    if (data?.success && data?.profile) {
      fullName.value = String(data.profile.fullName || '');
      sector.value = String(data.profile.sector || '');
      whatsapp.value = String(data.profile.whatsapp || '');
      save();
    }
  } catch (_) {
    // silencioso
  }
}

export function useUserProfile() {
  initLoad();
  return { email, empresaId, fullName, sector, whatsapp, isComplete, isIncomplete, setEmail, setEmpresaId, saveProfile, refreshFromServer };
}

import { ref, computed, watch } from 'vue';
import { useUserProfile } from '~/composables/useUserProfile';

export interface FavoriteItem {
  id: string | number;
  title?: string;
  brand?: string;
  alias?: string;
}

// Estado compartilhado em mdulo (persistido em localStorage e/ou servidor)
const items = ref<FavoriteItem[]>([]);

// Identidade do usurio (email + empresaId). Se disponvel, usa servidor.
const { email, empresaId } = useUserProfile();
const serverEnabled = computed(() => !!email.value && !!empresaId.value && Number(empresaId.value) > 0);

function getStorageKey() {
  const e = String(email.value || '').trim();
  const emp = Number(empresaId.value);
  if (e && Number.isFinite(emp)) return `favorite-items:${e}:${emp}`;
  return 'favorite-items';
}

function reloadFromLocal() {
  if (typeof window === 'undefined') return;
  try {
    const key = getStorageKey();
    const saved = window.localStorage.getItem(key);
    if (saved) {
      const parsed: FavoriteItem[] = JSON.parse(saved);
      if (Array.isArray(parsed)) items.value = parsed.filter(Boolean);
    } else {
      items.value = [];
    }
  } catch (_) {
    // ignore
  }
}

// Carrega inicialmente do storage (chaveado por usurio quando disponvel)
if (typeof window !== 'undefined') {
  // limpar chave legada no chaveada (evita vazamento entre usurios)
  try { window.localStorage.removeItem('favorite-items'); } catch (_) {}
  reloadFromLocal();
}

function persist() {
  if (typeof window !== 'undefined') {
    try {
      const key = getStorageKey();
      window.localStorage.setItem(key, JSON.stringify(items.value));
    } catch (_) {
      // ignore
    }
  }
}

async function reloadFromServer() {
  if (!serverEnabled.value) return;
  try {
    const data: any = await $fetch('/api/favoritos/list', {
      params: { email: String(email.value).toUpperCase(), empresaId: Number(empresaId.value) }
    });
    const list: FavoriteItem[] = Array.isArray(data?.items) ? data.items : [];
    items.value = list.map((it: any) => ({ id: it.id, alias: it.alias }));
    persist();
  } catch (err) {
    // fallback silencioso
  }
}

// Recarrega automaticamente quando o usurio est disponvel
if (typeof window !== 'undefined') {
  watch(serverEnabled, (v) => { if (v) reloadFromServer(); else reloadFromLocal(); }, { immediate: true });
  // Tambm recarrega localmente quando mudam email/empresa sem servidor
  watch([email, empresaId], () => { if (!serverEnabled.value) reloadFromLocal(); });
}

function has(id: FavoriteItem['id']) {
  return items.value.some((p) => p.id === id);
}

async function add(item: FavoriteItem) {
  if (item.id == null) return { ok: false as const, reason: 'invalid' as const };
  if (has(item.id)) return { ok: false as const, reason: 'exists' as const };
  if (serverEnabled.value) {
    try {
      const res: any = await $fetch('/api/favoritos/toggle', {
        method: 'POST', body: { email: String(email.value).toUpperCase(), empresaId: Number(empresaId.value), id: item.id }
      });
      if (res?.success) {
        await reloadFromServer();
        return { ok: true as const };
      }
    } catch (_) {}
    return { ok: false as const, reason: 'server' as const };
  }
  items.value.push({ id: item.id, title: item.title, brand: item.brand });
  persist();
  return { ok: true as const };
}

async function remove(id: FavoriteItem['id']) {
  if (serverEnabled.value) {
    try {
      const res: any = await $fetch('/api/favoritos/toggle', {
        method: 'POST', body: { email: String(email.value).toUpperCase(), empresaId: Number(empresaId.value), id }
      });
      if (res?.success) {
        await reloadFromServer();
        return;
      }
    } catch (_) {}
  }
  items.value = items.value.filter((p) => p.id !== id);
  persist();
}

async function toggle(item: FavoriteItem) {
  if (serverEnabled.value) {
    try {
      const res: any = await $fetch('/api/favoritos/toggle', {
        method: 'POST', body: { email: String(email.value).toUpperCase(), empresaId: Number(empresaId.value), id: item.id }
      });
      if (res?.success) {
        await reloadFromServer();
        return { ok: true as const, active: !!res.active };
      }
    } catch (_) {}
    return { ok: false as const, active: has(item.id) };
  }
  if (has(item.id)) {
    await remove(item.id);
    return { ok: true as const, active: false };
  } else {
    const res = await add(item);
    return { ok: res.ok, active: res.ok ? true : false };
  }
}

async function setAlias(id: FavoriteItem['id'], alias: string) {
  if (serverEnabled.value) {
    try {
      const res: any = await $fetch('/api/favoritos/alias', {
        method: 'POST', body: { email: String(email.value).toUpperCase(), empresaId: Number(empresaId.value), id, alias }
      });
      if (res?.success) {
        await reloadFromServer();
        return true;
      }
    } catch (_) {}
    return false;
  }
  // fallback local: persiste alias em localStorage
  const list = items.value.map((p) => (p.id === id ? { ...p, alias } : p));
  items.value = list;
  persist();
  return true;
}

const count = computed(() => items.value.length);

export function useFavorites() {
  return {
    items,
    add,
    remove,
    toggle,
    has,
    setAlias,
    count,
    refreshFromServer: reloadFromServer,
  };
}

